import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { FiMail, FiLock, FiEye, FiEyeOff } from 'react-icons/fi';
import './Auth.css';

const Login = () => {
  const [formData, setFormData] = useState({ email: '', password: '' });
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    // Mock authentication (replace with real API call)
    if (formData.email === 'user@example.com' && formData.password === 'password') {
      setLoading(false);
      navigate('/');
    } else {
      setLoading(false);
      setError('Invalid email or password');
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-box enhanced-auth-box">
        <div className="auth-logo">Codevo</div>
        <h1 className="auth-title">Sign In</h1>
        <p className="auth-subtitle">Welcome back! Please login to your account.</p>
        <form onSubmit={handleSubmit} className="auth-form">
          <div className="form-group input-icon-group">
            <span className="input-icon"><FiMail /></span>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              placeholder="Email address"
              required
              autoComplete="username"
            />
          </div>
          <div className="form-group input-icon-group">
            <span className="input-icon"><FiLock /></span>
            <input
              type={showPassword ? 'text' : 'password'}
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              placeholder="Password"
              required
              autoComplete="current-password"
            />
            <span className="input-icon input-icon-right" onClick={() => setShowPassword(v => !v)} tabIndex={0} role="button" aria-label="Toggle password visibility">
              {showPassword ? <FiEyeOff /> : <FiEye />}
            </span>
          </div>
          {error && <div className="auth-error">{error}</div>}
          <button type="submit" className="auth-button" disabled={loading}>
            {loading ? 'Signing In...' : 'Sign In'}
          </button>
        </form>
        <p className="auth-switch">
          Don't have an account?{' '}
          <Link to="/signup" className="auth-link">
            Sign Up
          </Link>
        </p>
      </div>
    </div>
  );
};

export default Login;
